{const a=document.querySelector("#open-button"),b=document.querySelector("#close-button"),c=document.querySelector("#overlay"),d=document.querySelector("#pop-value"),e=document.querySelector("#reset-button");function openPopup(e){null!=e&&(e.classList.add("active"),c.classList.add("active"))}function closePopup(e){null!=e&&(e.classList.remove("active"),c.classList.remove("active"))}a.addEventListener("click",()=>{openPopup(document.querySelector("#popup-content")),addCountTimes()}),b.addEventListener("click",()=>{closePopup(document.querySelector("#popup-content"))})}